INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (1,'Michael Jackson', '1958-10-29', 'Estats Units', 'https://www.hola.com/imagenes/biografias/michael-jackson/79629-jackson10.jpg', 'Michael Jackson va ser un cantant, compositor, productor, ballarí, actor i filantrop nord-americà. Conegut com el «rei del pop», les seves contribucions i el reconeixement en la història de la música i el ball durant més de quatre dècades.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (2, 'Red Hot Chili Peppers', '1983-01-01', 'Estats Units', 'https://static.posters.cz/image/750/posters/red-hot-chili-peppers-logo-i4622.jpg', 'Red Hot Chili Peppers és una banda de rock nord-americana formada el 1983 a Los Angeles, Califòrnia. Els seus integrants són, el vocalista Anthony Kiedis, el guitarrista John Frusciante, el baixista Flea i el baterista Txad Smith.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (3, 'Coldplay', '1996-01-01', 'Regne Unit', 'https://upload.wikimedia.org/wikipedia/commons/thumb/0/07/Coldplay_2017%2C_cropped_01.jpg/1200px-Coldplay_2017%2C_cropped_01.jpg', 'Coldplay és una banda britànica de pop rock i rock alternatiu formada a Londres el 1996. Està integrada per Chris Martin, Jon Buckland, Guy Berryman i Will Champion.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (4, 'The Chainsmokers', '2012-01-01', 'Estats Units', 'https://los40es00.epimg.net/los40/imagenes/2019/11/11/los40dance/1573472395_927980_1573472559_noticia_normal.jpg', 'The Chainsmokers és un duet de discjòqueis i productors originaris de Nova York, Estats Units, integrat per Andrew Taggart i Alex Pall.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (5, 'Don Diablo', '1980-02-27', 'Països Baixos', 'https://i1.sndcdn.com/avatars-q6M1ETwBbGfsiYKU-wDz2og-t500x500.jpg', 'Don Diablo és un DJ productor i compositor de EDM neerlandès. Actualment ocupa el lloc #7 a la enquesta realitzada el 2021 per la revista DJmag, i va obtenir el lloc #38 al lloc web 1001tracklists el 2021.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (6, 'Nirvana', '1987-01-01', 'Estats Units', 'https://indiehoy.com/wp-content/uploads/2020/09/nirvana-logo.jpg', 'Nirvana va ser una banda de grunge nord-americà procedent d''Aberdeen, Washington, Estats Units fundada pel vocalista i guitarrista Kurt Cobain i el baixista Krist Novoselic el 1987.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (7, 'Twenty One Pilots', '2009-01-01', 'Estats Units', 'https://mor.bo/wp-content/uploads/2020/06/twenty-one-pilots-9.jpg', 'Twenty One Pilots és un duo musical nord-americà de Columbus, Ohio. La banda es va formar el 2009 pel vocalista Tyler Joseph juntament amb Nick Thomas i Chris Salih, que se''n van anar el 2011.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (8, 'Daft Punk', '1993-01-01', 'França', 'https://emtstatic.com/2021/02/Daft-Punk.jpg', 'Daft Punk va ser un duo francès de french house format pels DJ i productors Guy-Manuel de Homem-Christo i Thomas Bangalter. Va aconseguir una gran popularitat dins del house a mitjans dels anys 90 a França.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (9, 'Pink Floyd', '1965-01-01', 'Regne Unit', 'https://studiosol-a.akamaihd.net/uploadfile/letras/fotos/0/9/3/b/093b9f9e35281efb9d86a1e33790947a.jpg', 'Pink Floyd va ser una banda de rock britànica, fundada a Londres el 1964. Considerada una icona cultural del segle XX i una de les bandes més influents, reeixides i aclamades en la història de la música popular.');
INSERT INTO ARTISTES (id_artista, nom_artista, data_naixement, localitat, foto, biografia) VALUES (10, 'Billie Eilish', '2001-12-18', 'Estats Units', 'https://www.hola.com/imagenes/belleza/actualidad/20210318186309/billie-eilish-cambio-de-look-radical-rubia/0-932-93/billie-eilish-getty2-t.jpg', 'Billie Eilish és una cantant i compositora nord-americana. Va adquirir fama com a artista quan tenia 13 anys, arran del senzill «Ocean Eyes» que es va publicar el 2015 a SoundCloud a la edat de 14 anys.');
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (1, 'Thriller', '12.64', '50', true, 'https://m.media-amazon.com/images/I/71Y24VlCZGL._SL1500_.jpg', '', 'https://open.spotify.com/embed/album/2ANVost0y2y52ema1E9xAZ', 'Thriller és el sisè àlbum de estudi del artista nord-americà Michael Jackson, publicat el 30 de novembre de 1982 per Epic Records.', 2, 1);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (2, 'Californication', '7.58', '50', true, 'https://m.media-amazon.com/images/I/81TnWHafWdL._SL1448_.jpg', '', 'https://open.spotify.com/embed/album/0fLhefnjlIV3pGNF9Wo8CD', 'Californication és el setè àlbum de estudi de la banda nord-americana de rock alternatiu i funk rock Red Hot Chili Peppers. Rick Rubin ho va produir i Warner Bros. Records ho va llançar al mercat el 8 de juny de 1999.', 3, 2);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (3, 'A Head Full Of Dreams', '6.99', '50', true, 'https://m.media-amazon.com/images/I/A1p7bWyAvmL._SL1500_.jpg', '', 'https://open.spotify.com/embed/album/3cfAM8b8KqJRoIzt3zLKqw', 'A Head Full Of Dreams és el setè àlbum de estudi de la banda britànica de pop i rock alternatiu Coldplay. Va ser llançant comercialment el 4 de desembre de 2015, per les discogràfiques Parlophone i Atlantic Records.', 2, 3);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (4, 'World War Joy', '8.99', '50', true, 'https://m.media-amazon.com/images/I/71iM5Ckid3L._SL1200_.jpg', '', 'https://open.spotify.com/embed/album/01GR4NL5O5CZM51k0aejKD', 'World War Joy és un àlbum de estudi del duo nord-americà The Chainsmokers. Es va llançar el 6 de desembre de 2019, distribuït a través de Disruptor i Columbia Records, amb col·laboracions com Illenium, Lennon Stella, Bebe Rexha, Ty Dolla $ign...', 1, 4);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (5, 'Forever', '12.64', '50', true, 'https://majomontemayor.com/wp-content/uploads/2021/09/image-1024x1024.jpeg', '', 'https://open.spotify.com/embed/album/53F4qZCDBwulb9eoMuWHf2', 'FORΞVΞR, és el nou àlbum del DJ/Productor dels Països Baixos Don Diablo, va presentar el seu àlbum més esperat en què el artista desprèn tot el seu nivell en la producció.', 1, 5);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (6, 'Nevermind', '9.99', '50', true, 'https://m.media-amazon.com/images/I/71DQrKpImPL._SL1400_.jpg', '', 'https://open.spotify.com/embed/album/2UJcKiJxNryhL050F5Z1Fk', 'Nevermind és el segon àlbum de estudi de la banda nord-americana de grunge Nirvana, publicat el 24 de setembre de 1991, igual que Badmotorfinger, el segon àlbum d''estudi de Soundgarden.', 3, 6);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (7, 'Blurryface', '9.91', '50', true, 'https://m.media-amazon.com/images/I/71k-5WHYB9L._SL1425_.jpg', '', 'https://open.spotify.com/embed/album/3cQO7jp5S9qLBoIVtbkSM1', 'BLVRRYFΛCE, és el quart àlbum de llarga durada i segon àlbum de estudi del duo musical nord-americà Twenty One Pilots, llançat el 17 de maig de 2015, a través de Fueled by Ramen.', 2, 7);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (8, 'Random Access Memories', '8.99', '50', true, 'https://m.media-amazon.com/images/I/61Uxg-SWExL._SL1500_.jpg', '', 'https://open.spotify.com/embed/album/4m2880jivSbbyEGAKfITCa', 'Random Access Memories és el quart i darrer àlbum de estudi del duo francès Daft Punk. Es va publicar oficialment el 17 de maig a Austràlia, el 20 de maig al Regne Unit i el 21 de maig de 2013 als Estats Units, sota llicència de Daft Life.', 1, 8);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (9, 'The Wall', '11.43', '50', true, 'https://m.media-amazon.com/images/I/51WrG48A3tL.jpg', '', 'https://open.spotify.com/embed/album/6WaIQHxEHtZL0RZ62AuY0g', 'The Wall és el onzè àlbum de estudi de la banda britànica de rock progressiu Pink Floyd i el segon doble, publicat el 1979. Es va gravar sota la direcció del productor Bob Ezrin i dels membres de Pink Floyd David Gilmour i Roger Waters.', 3, 9);
INSERT INTO ALBUMS (id_album, nom_album, preu, unitats, en_stock, foto, discografia, playlist, descripcio, id_categoria, id_artista) VALUES (10, 'Happier Than Ever', '13.65', '50', true, 'https://m.media-amazon.com/images/I/71laVfWEkCS._SL1400_.jpg', '', 'https://open.spotify.com/embed/album/0JGOiO34nwfUdDrD612dOp', 'Happier Than Ever és el segon àlbum de estudi de la cantautora nord-americana Billie Eilish, el llançament del qual va tenir lloc el 30 de juliol de 2021. Eilish va coescriure el àlbum amb el seu productor i germà Finneas OConnell.', 2, 10);